//#region imports
import { Injectable } from '@angular/core';
import {
  SkyAuthGetTokenArgs,
  SkyAuthToken,
  SkyAuthTokenContextArgs,
} from '@skyux/http';

import jwtDecode from 'jwt-decode';

/**
 * Provides a mock token to downstream consumers for unit testing purposes.
 */
@Injectable()
export class SkyAuthTokenMockProvider {
  /* tslint:disable-next-line:max-line-length */
  public static readonly MOCK_TOKEN =
    'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..QjrwXaOhNpi8eRJpvHo_5XE7Jqs4nY1IkNs3nOs0fGI';

  public getToken(args?: SkyAuthGetTokenArgs): Promise<string> {
    return Promise.resolve(SkyAuthTokenMockProvider.MOCK_TOKEN);
  }

  public getDecodedToken(args?: SkyAuthGetTokenArgs): Promise<SkyAuthToken> {
    return this.getToken(args).then((token) => {
      return this.decodeToken(token);
    });
  }

  public getContextToken(args?: SkyAuthTokenContextArgs): Promise<string> {
    return this.getToken();
  }

  public getDecodedContextToken(
    args?: SkyAuthTokenContextArgs
  ): Promise<SkyAuthToken> {
    return this.getDecodedToken();
  }

  public decodeToken(token: string): SkyAuthToken {
    return jwtDecode<SkyAuthToken>(token);
  }
}
